insert into `dw_bbs_topics` ( `id`,`fid`,`user_id`,`status`,`username`,`name`,`content`,`posttime`,`edittime`,`ordertime`,`last_replytime`,`last_replyuser`,`last_replyusername`,`type`,`posts_num`,`hits`,`islock`,`isgood`,`istop`,`isalltop`,`stamp`,`isrecycle`,`credit`,`verifystate`,`verifydesc`,`isresolved`,`attachicon`,`highlight`,`addtime`,`addip`) values ("27","2","86","1","lotaies","红岭创投网站交易数据（2011年11月1日更新)","来源：红岭创投 \r\n\r\n \r\n[b][url=]红岭创投网站交易数据（2011年11月1日更新)[/url][/b]\r\n[align=left]\r\n[size=12pt]一.借款金额及收支余额统计:[/size] [/align][align=left][size=12pt]2009[/size][size=12pt]年二季度,借款总金额43.45万元（收支余额1.41万元）[/size] [/align][align=left][size=12pt]2009[/size][size=12pt]年三季度,借款总金额224.21万元（收支余额5.90万元）[/size] [/align][align=left][size=12pt]2009[/size][size=12pt]年四季度,借款总金额622.28万元（收支余额13.44万元）[/size] [/align][align=left][size=12pt]2010[/size][size=12pt]年一季度,借款总金额1047.50万元（收支余额21.42万元）[/size] [/align][align=left][size=12pt]2010[/size][size=12pt]年二季度,借款总金额1524.43万元（收支余额38.14万元）[/size] [/align][align=left][size=12pt]2010[/size][size=12pt]年三季度,借款总金额2692.98万元（收支余额67.24万元）[/size] [/align][align=left][size=12pt]2010[/size][size=12pt]年四季度,借款总金额3448.98万元（收支余额[/size][size=12pt]-9.14[/size][size=12pt]万元）[/size] [/align][align=left][size=12pt]2011[/size][size=12pt]年一季度,借款总金额4421.46万元（收支余额93.55万元）[/size] [/align][align=left][size=12pt]2011[/size][size=12pt]年二季度,借款总金额7223.83万元（收支余额2**.**万元）[/size] [/align][align=left][size=12pt]2011[/size][size=12pt]年三季度,借款总金额10767.63万元（收支余额4**.**万元）[/size] [/align][align=left][size=12pt]2011[/size][size=12pt]年四季度,借款总金额3952.29万元（收支余额1**.**万元）[/size] [/align][align=left][size=12pt]2009[/size][size=12pt]年3月28日[/size][size=12pt]至2011年10月31日24点数据,[/size][size=12pt]借款本金总额[/size][size=12pt]为36012.03万元[/size] [/align][align=left][size=12pt]（收支余额为已经去除坏帐垫付的交易收入)[/size] [/align]\r\n[align=left][size=12pt]二.坏帐垫付统计[/size] [/align][align=left][size=12pt]2011[/size][size=12pt]年10月31日[/size][size=12pt]24[/size][size=12pt]：00数据[/size] [/align][align=left][size=12pt]垫